package client;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.*;import music.MusicPlayer;import javax.swing.GroupLayout.Alignment;public class MainScreen extends JFrame {	public final int SCREEN_WIDTH = 1280;	public final int SCREEN_HEIGHT = 720;	Container content;	Background back = new Background();	JTextField tf;	JButton btn1, btn2, btn3, btn4, btn5, btn6;	protected MainScreen() {		this.mainScreen();	}		public void mainScreen() {		JPanel pan = new JPanel();		pan.setBounds(0, 620, 1280, 60);		pan.setLayout(new GridLayout(1, 6));		pan.setOpaque(false);		add(pan);		JButton btn1 = new JButton(new ImageIcon(MainScreen.class.getResource("../img/Allin.PNG")));		btn1.setOpaque(false);		pan.add(btn1);		JButton btn2 = new JButton(new ImageIcon(MainScreen.class.getResource("../img/Half.PNG")));		btn2.setOpaque(false);		pan.add(btn2);		JButton btn3 = new JButton(new ImageIcon(MainScreen.class.getResource("../img/Quater.PNG")));		btn3.setOpaque(false);		pan.add(btn3);		JButton btn4 = new JButton(new ImageIcon(MainScreen.class.getResource("../img/Bbing.PNG")));		btn4.setOpaque(false);		pan.add(btn4);		JButton btn5 = new JButton(new ImageIcon(MainScreen.class.getResource("../img/Call.PNG")));		btn5.setOpaque(false);		pan.add(btn5);		JButton btn6 = new JButton(new ImageIcon(MainScreen.class.getResource("../img/Die.PNG")));		btn6.setOpaque(false);		pan.add(btn6);				ActionListener action = new ActionListener() {			@Override			public void actionPerformed(ActionEvent e) {				System.out.println(e.getSource());				System.out.println(btn1);				if (e.getSource() == btn1) {					tf.setText("올인 배팅");				} else if (e.getSource() == btn2) {					tf.setText("하프 배팅");				} else if (e.getSource() == btn3) {					tf.setText("쿼터 배팅");				} else if (e.getSource() == btn4) {					tf.setText("삥 배팅");				} else if (e.getSource() == btn5) {					tf.setText("콜 배팅");				} else if (e.getSource() == btn6) {					tf.setText("다이 배팅");				}			}		};		btn1.addActionListener(action);		btn2.addActionListener(action);		btn3.addActionListener(action);		btn4.addActionListener(action);		btn5.addActionListener(action);		btn6.addActionListener(action);		JPanel pan2 = new JPanel();		pan2.setBounds(0, 230, 350, 150);		add(pan2);		JPanel pan3 = new JPanel();		pan3.setBounds(0, 50, 350, 150);		add(pan3);		JPanel pan4 = new JPanel();		pan4.setBounds(910, 50, 350, 150);		add(pan4);		JPanel pan5 = new JPanel();		pan5.setBounds(910, 230, 350, 150);		add(pan5);		JPanel pan6 = new JPanel();		pan6.setBounds(410, 100, 440, 300);		tf = new JTextField(15);		pan.add(tf);		tf.setBounds(300, 300, 400, 150);		pan6.add(tf);		add(pan6);		JPanel pan7 = new JPanel();		pan7.setBounds(450, 450, 350, 150);		add(pan7);		setTitle("섯다 온라인 게임");		back.mainImage();		content = getContentPane();		content.add(back, BorderLayout.CENTER);		back.setOpaque(false);		setSize(SCREEN_WIDTH, SCREEN_HEIGHT);		setResizable(false);		setVisible(true);		new Thread(new MusicPlayer()).start();		Toolkit toolkit = Toolkit.getDefaultToolkit();		Image img = toolkit.getImage(MainScreen.class.getResource("../img/titleIcon.jpg"));		setIconImage(img);		GroupLayout groupLayout = new GroupLayout(getContentPane());		groupLayout.setHorizontalGroup(				groupLayout.createParallelGroup(Alignment.LEADING).addGap(0, 1274, Short.MAX_VALUE));		groupLayout				.setVerticalGroup(groupLayout.createParallelGroup(Alignment.LEADING).addGap(0, 691, Short.MAX_VALUE));		getContentPane().setLayout(groupLayout);	}	public static void main(String[] args) {		new MainScreen();	}}